    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       String ProductName=pname.getText();
    String ProductId=pid.getText();
    String ProductPrice=pprice.getText();
    String ProductQuantity=pqty.getText();
    PreparedStatement p;
    
   String query ="UPDATE addpro set `pid`=?,`pprice`=?,`pqty`=? WHERE `pname`=?";
 
    try{
        p=Connection.getConnection().prepareStatement(query);
        p.setString(1, ProductName);
        p.setString(2, ProductId);
        p.setString(3, ProductPrice);
        p.setString(4, ProductQuantity);
     if(p.executeUpdate()>0){
         JOptionPane.showMessageDialog(null, "UPDATED PRODUCT");
         }  
         else{
             JOptionPane.showMessageDialog(null,"PRODUCT NOT FOUND");
         }
         }catch(Exception e){
             System.out.println(e);
         }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String ProductName=pname.getText();   
        ResultSet rs;
try{
    String query="select* from `addpro`where `pname`=?";
    PreparedStatement ps=Connection.getConnection().prepareStatement(query);
    ps.setString(1,ProductName);
    rs=ps.executeQuery();
    if(rs.next()){
        pid.setText(rs.getString(3));
        pprice.setText(rs.getString(4));
        pqty.setText(rs.getString(5));
    }
    else{
        JOptionPane.showMessageDialog(null,"product not exist");
    }
    }
catch(Exception ex){
    System.out.println(ex);
}
    }//GEN-LAST:event_jButton1ActionPerformed

