    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
       String ProductName=pname.getText();
    int s=JOptionPane.showConfirmDialog(null,"Do you want to delete","select",JOptionPane.YES_NO_OPTION);
     if(s==0){
    String query="Delete from addpro where `pname`=?";
  
    try{
      PreparedStatement p=Connection.getConnection().prepareStatement(query);
                p.setString(1, ProductName);
     if(p.executeUpdate()>0){
         pid.setText("");
         pprice.setText("");
         pqty.setText("");
     
         JOptionPane.showMessageDialog(null,"Delete Product");
     }
     else{
         JOptionPane.showMessageDialog(null,"Product Name not exist");
     }
    }
        catch(Exception ex){
                System.out.println(ex);
                }
     }
    }//GEN-LAST:event_jButton3ActionPerformed

